import React from "react";
import "./App.css";
import {useFormik} from "formik";

function App() {
  const formik = useFormik({
    initialValues: {
      fullName: '',
      stuName: '',
      emailId: '',
      cityName: ''
    },
    onSubmit: values =>{
      // alert(JSON.stringify(values))
      const kuch = [{
        fullName: values.fullName,
        stuName: '',
        emailId: '',
        cityName: ''
      }]
      localStorage.setItem('fullName', formik)
    } 
  })
  return (
    <div>
      <div className="main">
        <h1>Form</h1>
      </div>

      <div>
        <form onSubmit={formik.handleSubmit}>
          <label>Full Name:</label>
          <input onChange={formik.handleChange} value={formik.values.fullName} id="fullName" type="text" name="fullName"></input>

          <label>Student Code:</label>
          <input onChange={formik.handleChange} value={formik.values.stuName} id="stuName" type="text" name="stuName"></input>

          <label>Email ID:</label>
          <input onChange={formik.handleChange} value={formik.values.emailId} id="emailId" type="text" name="emailId"></input>

          <label>City:</label>
          <input onChange={formik.handleChange} value={formik.values.cityName} id="cityName" type="text" name="cityName"></input>

          <button type="submit">Add</button>
        </form>
      </div>
    </div>
  );
}

export default App;
